// S3から取得した画像のメタデータを解析してアスペクト比を計算
const items = $input.all();
const results = [];

// Sharp画像処理ライブラリを使用（n8nに組み込まれている場合）
// または基本的な画像メタデータ解析を実行

for (const item of items) {
  let imageData = null;
  let aspectRatio = null;
  let orientation = null;

  try {
    // バイナリデータから画像情報を取得
    if (item.binary && item.binary.image_data) {
      const binaryData = item.binary.image_data;

      // 画像のバイナリデータから寸法を抽出する処理
      // 注意: 実際の実装では、画像処理ライブラリが必要

      let width = null;
      let height = null;

      // 1. リクエストで寸法が指定されている場合
      if (item.json.width && item.json.height) {
        width = parseInt(item.json.width);
        height = parseInt(item.json.height);
      } else if (item.json.image_width && item.json.image_height) {
        width = parseInt(item.json.image_width);
        height = parseInt(item.json.image_height);
      } else {
        // 2. 画像ファイル名から推測する場合（例: image_1920x1080.jpg）
        const fileName = item.json.file_key || '';
        if (fileName) {
          const dimensionMatch = fileName.match(/(\d+)x(\d+)/);
          if (dimensionMatch) {
            width = parseInt(dimensionMatch[1]);
            height = parseInt(dimensionMatch[2]);
          }
        }

        // 3. 一般的な画像サイズの推測（ファイル名パターン）
        if ((!width || !height) && fileName) {
          if (fileName.includes('banner') || fileName.includes('header')) {
            width = 1920; height = 1080; // デフォルト横長
          } else if (fileName.includes('portrait') || fileName.includes('mobile')) {
            width = 1080; height = 1920; // デフォルト縦長
          } else if (fileName.includes('square') || fileName.includes('icon') || fileName.includes('profile')) {
            width = 500; height = 500; // デフォルト正方形
          } else {
            // 4. バイナリデータのサイズから推測（概算）
            const fileSize = binaryData.fileSize || binaryData.data?.length || 0;
            if (fileSize > 0) {
              // 簡易的な推測ロジック（JPEGの場合）
              if (fileSize > 2000000) { // 2MB以上
                width = 1920; height = 1080;
              } else if (fileSize > 500000) { // 500KB以上
                width = 1024; height = 768;
              } else {
                width = 500; height = 500;
              }
            }
          }
        }
      }

      if (width && height && width > 0 && height > 0) {
        aspectRatio = width / height;

        // アスペクト比による分類
        if (aspectRatio > 1.5) {
          orientation = 'wide'; // 横長（16:9など）
        } else if (aspectRatio < 0.75) {
          orientation = 'tall'; // 縦長（9:16など）
        } else {
          orientation = 'square'; // 正方形に近い（4:3、1:1など）
        }

        imageData = {
          width: width,
          height: height,
          aspectRatio: aspectRatio,
          orientation: orientation,
          ratio_text: `${width}:${height}`,
          decimal_ratio: Math.round(aspectRatio * 100) / 100,
          file_size: binaryData.fileSize || 0,
          mime_type: binaryData.mimeType || 'unknown',
          s3_source: {
            bucket: item.json.bucket_name,
            key: item.json.file_key
          }
        };
      } else {
        // 画像データが取得できない場合
        imageData = {
          width: null,
          height: null,
          aspectRatio: null,
          orientation: 'unknown',
          ratio_text: 'unknown',
          decimal_ratio: null,
          error: 'Could not determine image dimensions from S3 file',
          file_size: binaryData.fileSize || 0,
          mime_type: binaryData.mimeType || 'unknown',
          s3_source: {
            bucket: item.json.bucket_name,
            key: item.json.file_key
          }
        };
      }
    } else {
      // S3からの取得に失敗した場合
      imageData = {
        error: 'Failed to download image from S3',
        orientation: 'error',
        s3_source: {
          bucket: item.json.bucket_name,
          key: item.json.file_key
        }
      };
    }

    results.push({
      json: {
        ...item.json,
        image_analysis: imageData
      },
      binary: item.binary
    });

  } catch (error) {
    results.push({
      json: {
        ...item.json,
        image_analysis: {
          error: `S3 Image Analysis Error: ${error.message}`,
          orientation: 'error',
          s3_source: {
            bucket: item.json.bucket_name,
            key: item.json.file_key
          }
        }
      },
      binary: item.binary
    });
  }
}

return results;

// 横長画像の処理とS3への結果保存
const items = $input.all();
const results = [];

for (const item of items) {
  const analysis = item.json.image_analysis;

  results.push({
    json: {
      ...item.json,
      processing_result: {
        category: 'wide_image',
        message: `S3から横長画像を検出しました。アスペクト比: ${analysis.decimal_ratio}`,
        recommended_action: 'バナー画像やヘッダー画像として使用することをお勧めします',
        dimensions: `${analysis.width}x${analysis.height}`,
        file_info: {
          size: analysis.file_size,
          mime_type: analysis.mime_type,
          s3_location: `s3://${analysis.s3_source.bucket}/${analysis.s3_source.key}`
        },
        processed_at: new Date().toISOString(),
        processing_type: 's3_source'
      }
    },
    binary: item.binary
  });
}

return results;

