// S3エラーまたは不明な画像の処理
const items = $input.all();
const results = [];

for (const item of items) {
  const analysis = item.json.image_analysis;

  results.push({
    json: {
      ...item.json,
      processing_result: {
        category: 'unknown_or_error',
        message: 'S3画像の分析中にエラーが発生したか、画像の種類を特定できませんでした',
        recommended_action: 'S3のファイルパスと画像ファイルを確認してください',
        error_details: analysis?.error || 'Unknown error',
        file_info: analysis?.s3_source ? {
          s3_location: `s3://${analysis.s3_source.bucket}/${analysis.s3_source.key}`,
          size: analysis.file_size || 0,
          mime_type: analysis.mime_type || 'unknown'
        } : null,
        processed_at: new Date().toISOString(),
        processing_type: 's3_source'
      }
    },
    binary: item.binary
  });
}

return results;
